mapper.select:
SELECT A.FNNO, A.GUBUN, GET_CODE_NAME('CH012',A.GUBUN) AS GUBUN_NM, A.CATEGORY, GET_CODE_NAME(CASE WHEN A.GUBUN ='FN010' THEN 'CH005' ELSE 'CH006' END , A.CATEGORY) AS CATEGORY_NM, A.SUB_MEMO, A.FNDATE, CAST(CASE WHEN A.GUBUN ='FN010' THEN A.MONEY ELSE 0 END AS UNSIGNED) AS IN_MONEY, CAST(CASE WHEN A.GUBUN ='FN020' THEN A.MONEY ELSE 0 END AS UNSIGNED) AS OUT_MONEY, @BF_SUM := @BF_SUM + CASE WHEN GUBUN ='FN020' THEN A.MONEY * -1 ELSE A.MONEY END AS REST_MONEY, /*누적합계*/ A.MONEY, A.MEMO, A.OFFERTORY_DATE, A.DEL_YN, A.DEL_ID, A.CR_ID, A.CR_DT, A.UP_ID, A.UP_DT FROM CH_FINANCE A, (SELECT @PARTITION:='', @BF_SUM:=0 ,@ROWNUM:=0 FROM DUAL) B WHERE 1=1
AND A.GUBUN = #{srch_gubun}
AND A.CATEGORY = #{srch_category}
AND DATE_FORMAT(A.FNDATE, '%Y-%m') = #{srch_date}
AND A.SUB_MEMO = #{srch_sub_memo}
AND A.DEL_YN='N' ORDER BY A.FNDATE ASC, A.FNNO ASC

SELECT A.FNNO, A.GUBUN, GET_CODE_NAME('CH012',A.GUBUN) AS GUBUN_NM, A.CATEGORY, GET_CODE_NAME(CASE WHEN A.GUBUN ='FN010' THEN 'CH005' ELSE 'CH006' END , A.CATEGORY) AS CATEGORY_NM, A.SUB_MEMO, A.FNDATE, CAST(CASE WHEN A.GUBUN ='FN010' THEN A.MONEY ELSE 0 END AS UNSIGNED) AS IN_MONEY, CAST(CASE WHEN A.GUBUN ='FN020' THEN A.MONEY ELSE 0 END AS UNSIGNED) AS OUT_MONEY, @BF_SUM := @BF_SUM + CASE WHEN GUBUN ='FN020' THEN A.MONEY * -1 ELSE A.MONEY END AS REST_MONEY, /*누적합계*/ A.MONEY, A.MEMO, A.OFFERTORY_DATE, A.DEL_YN, A.DEL_ID, A.CR_ID, A.CR_DT, A.UP_ID, A.UP_DT FROM CH_FINANCE A, (SELECT @PARTITION:='', @BF_SUM:=0 ,@ROWNUM:=0 FROM DUAL) B WHERE 1=1
AND A.GUBUN = #{srch_gubun}
AND A.CATEGORY = #{srch_category}
AND FNDATE >= #{srch_financeDateFrom}
AND FNDATE <= #{srch_financeDateTo}
AND A.SUB_MEMO like CONCAT('%' , #{srch_subMemo} , '%')
AND A.DEL_YN='N' ORDER BY A.FNDATE ASC, A.FNNO ASC
