mapper.select:
SELECT * FROM CH_FAMILY
WHERE FAMILY_CODE = #{FAMILY_CODE}
ORDER BY CR_DT

mapper.insert:
INSERT INTO CH_FAMILY(
	FAMILY_CODE, USER_CODE, USER_NM, RELATION, GENDER, 
	BIRTHDAY, MOBILE, BAPTISM, DUTY, RMK, 
	CR_ID, CR_DT
) VALUES (
	#{FAMILY_CODE}, #{USER_CODE}, #{USER_NM}, #{RELATION}, #{GENDER}, 
	#{BIRTHDAY}, #{MOBILE}, #{BAPTISM}, #{DUTY}, #{RMK}, 
	#{SESS_EMAIL}, NOW()
);

mapper.delete:
DELETE FROM CH_FAMILY
WHERE FAMILY_CODE = #{FAMILY_CODE}
AND USER_CODE = #{USER_CODE}
AND USER_NM = #{USER_NM}

mapper.select:
SELECT A.PRNO, A.USER_CODE, A.USER_NM, A.GUBUN, GET_CODE_NAME('CH007',A.GUBUN) AS GUBUN_NM, A.STATUS, GET_CODE_NAME('CH008',A.STATUS) AS STATUS_NM, A.SUBJECT, A.SDATE, A.EDATE, A.MEMO, A.CR_DT, A.CR_ID, A.UP_DT, A.UP_ID, (SELECT COUNT(C.DTNO) FROM CH_PRAYER_DT C WHERE C.PRNO =A.PRNO ) AS DT_CNT, (@ROWNUM:=@ROWNUM+1) AS ROW_NO FROM CH_PRAYER A , (SELECT @ROWNUM:=0 FROM DUAL) B WHERE 1=1
AND A.GUBUN = #{srch_gubun}
AND A.STATUS = #{srch_status}
AND A.USER_NM LIKE CONCAT('%' , #{scrh_user_nm} , '%')
AND A.USER_SUBJECT LIKE CONCAT('%' , #{scrh_subject} , '%')
AND A.PRNO = #{srch_prno}
ORDER BY A.PRNO DESC

mapper.update:
UPDATE CH_PRAYER_DT SET
	MEMO = #{frm_dt_memo},
	UP_DT = now(),
	UP_ID = #{SESS_EMAIL}
WHERE DTNO = #{frm_dt_dtno}
AND PRNO = #{frm_dt_prno}

mapper.delete:
DELETE FROM CH_PRAYER_DT
WHERE DTNO = #{frm_dt_dtno}
AND PRNO = #{frm_dt_prno}
